<template>
  <img :src="isload ? cSrc : defaultSrc">
</template>
<script>
export default {
  data() {
    return {
      isload: false,
      cSrc: ""
    };
  },
  props: {
    src: String,
    defaultSrc: {
      type: String,
      default:
        "data:image/png;base64,"
    }
  },
  methods: {
    loadUrl(url) {
      let Img = new Image();
      Img.src = url;
      Img.onload = () => {
        this.cSrc = url;
        this.isload = true;
      };
    }
  },
  watch: {
    src(val) {
      if(val) {
        this.loadUrl(val)
      }
    }
  },
  mounted() {
    if(this.src) {
      this.loadUrl(this.src)
    }
  }
};
</script>
